function gravity(value, sender)
    sender["target"] = DEFAULT
    if wo["IsDifficult"] then
	wo["GlobalForce"] = no[sender["_doorid"]]["_gravity"]
    else
	wo["GlobalForce"] = 0.5 * no[sender["_doorid"]]["_gravity"]
    end
    no[sender["_doorid"]]:open()
end
