el:protected.el:luamain:
wo["ConserveLevel"] = true

ti["r"] = {"fl-rough-red"}
ti["b"] = {"fl-rough-blue"}
ti["."] = ti({"fl-rough-blue", checkerboard=0}) .. {"fl-rough-red", checkerboard=1}

ti[" "] = ti["."] .. {"it_blocker"}
ti["w"] = {"st-wood"}
ti["s"] = {"st_switch", target="rmfloor"}
ti["i"] = {"st_switch", target="icefloor"}

ti["1"] = ti["b"] .. {"#ac-blackball"}
ti["2"] = ti["b"] .. {"#ac-bug", "mybug"}

function myresolver(key, x, y)
    if key == " " then
        if math.random() >= 0.5 then
            return ti["b"]
        else
            return ti["r"]
        end
    else
        return ti[key]
    end
end

resolver = ti
if wo["IsDifficult"] then
    resolver = myresolver
end

w, h = wo(resolver, " ", {
"                    ",
" w     bbbbb        ",
" w  bbbrrrrrbbb  ",
"   bbrr.....rrbb ",
"   br.........rb    ",
"  br...........rb   ",
"  br...........rb   ",
"  br...........rb   ",
"   br.........rb    ",
"   bbrr.....rrbb    ",
"    b2brrrrrb1b     ",
"       bbbbb        ",
"si                  "
})

function icefloor(value, sender)
    wo[no["mybug"]] = {"fl-ice"}
end

function rmfloor(value, sender)
    wo[no["mybug"]] = {"fl_nil"}
end
