function setlaserswitch()
    wo[no["LaserP"] + {4,0}] = ti["p"]
    -- the new laserswitch will not cause an action if directly illuminated
    -- do it manually if we have need of
    if (no["newlaserswitch"]["state"] == ON) then
        no["LaserR"]:on()
    else
        no["LaserR"]:off()
    end
end

function postinit()
    no["LaserN"]:on()
end
