function push(value, sender)   -- test for block on moves on falling
    no["i1"]:signal(1)
    no["i2"]:signal(1)
    no["k1"]:signal(1)
    no["k2"]:signal(1)
end

function flood(value, sender)
    for i = 1, 6 do
        wo[sender - i*po(0,1)] = {"fl_water"}
    end
end
