aby = no["ab#*"]
abypos = po(aby)

function doabyss()
    for i = 1, #abypos do
       wo[abypos[i]] = {"it_cherry"}
    end
end

function doitem()
    it(abypos):kill()
    wo[abypos] = {"st_brake"}
end

function dopos()
    p = po["water"]
    if p == nil then
       print "water is nil"
    else
       print ("water is not nil but type " .. type(p) .. " usertype ".. usertype(p))
    end
    print("water at "..p.x.." - "..p.y)
    wo[po["water"]] = {"it_cherry"}
end

function dopw()
    print ("number of pws "..#po["pw#*"])
    wo[po["pw#*"]] = {"it_cherry"}
end

po["emil"] = po(2, 10)
wo[po["emil"]] = {"it_cherry"}

function dojoin()
    wo[abypos .. po["pw#*"]] = {"it_banana"}
end
